import React, { useState } from "react";

const RegistrationBox: React.FC = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    phone: "",
    password: "",
    address: "",
    city: "",
    state: "",
    pincode: "",
    agree: false,
  });

  const [error, setError] = useState("");

 const handleChange = (
  e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
) => {
  const { name, value, type } = e.target;
  const isCheckbox = type === "checkbox";
  const checked = isCheckbox && (e.target as HTMLInputElement).checked;

  if (name === "phone") {
    const numericValue = value.replace(/\D/g, "").slice(0, 10); // Max 10 digits
    setFormData((prev) => ({ ...prev, phone: numericValue }));
    return;
  }

  if (name === "pincode") {
    const numericValue = value.replace(/\D/g, "").slice(0, 6); // Max 6 digits
    setFormData((prev) => ({ ...prev, pincode: numericValue }));
    return;
  }

  setFormData((prev) => ({
    ...prev,
    [name]: isCheckbox ? checked : value,
  }));
};


const handleSubmit = (e: React.FormEvent) => {
  e.preventDefault();
  setError("");
const passwordRegex = /^(?=.*[0-9])(?=.*[^A-Za-z0-9])([^\s]{8,15})$/;

if (!passwordRegex.test(password)) {
  setError(
    "Password must be 8-15 characters, include a number, a special character, and have no spaces."
  );
  return;
}
  const {
    fullName,
    email,
    phone,
    password,
    address,
    city,
    state,
    pincode,
    agree,
  } = formData;

  if (
    !fullName ||
    !email ||
    !phone ||
    !password ||
    !address ||
    !city ||
    !state ||
    !pincode
  ) {
    setError("Please fill out all the fields.");
    return;
  }

  if (phone.length !== 10) {
    setError("Phone number must be exactly 10 digits.");
    return;
  }

  if (pincode.length !== 6) {
    setError("Pincode must be exactly 6 digits.");
    return;
  }

  if (!agree) {
    setError("You must agree to the terms and privacy policy.");
    return;
  }

  // ✅ All validations passed
  console.log("Form submitted:", formData);
};

  return (
    <div className="min-h-screen bg-indigo-50 flex justify-center items-start px-4 py-10">
      <div className="bg-white p-0 rounded-2xl max-w-2xl w-full shadow-lg overflow-hidden">
        {/* Header */}
        <div className="bg-indigo-600 p-6">
          <h2 className="text-2xl font-bold text-white mb-1">Register for Quizoraa</h2>
          <p className="text-indigo-100 text-sm">Create your account to participate in monthly contests</p>
        </div>

        {/* Form */}
        <div className="p-6">
          <form className="flex flex-col" onSubmit={handleSubmit}>
            {error && <div className="text-red-600 font-medium mb-4">{error}</div>}

            <div className="flex flex-wrap gap-4 mb-5">
              <div className="flex flex-col flex-1 min-w-[200px]">
                <label className="font-medium mb-1">Full Name</label>
                <input
                  type="text"
                  name="fullName"
                  value={formData.fullName}
                  onChange={handleChange}
                  placeholder="Enter your name"
                  className="p-3 border border-gray-300 rounded-lg"
                />
              </div>
              <div className="flex flex-col flex-1 min-w-[200px]">
                <label className="font-medium mb-1">Email Address</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="Enter your email"
                  className="p-3 border border-gray-300 rounded-lg"
                />
              </div>
            </div>

            <div className="flex flex-wrap gap-4 mb-5">
              <div className="flex flex-col flex-1 min-w-[200px]">
                <label className="font-medium mb-1">Phone Number</label>
               <input
                type="tel"
  name="phone"
  inputMode="numeric"
  value={formData.phone}
  onChange={handleChange}
  placeholder="Enter your phone"
  className="p-3 border border-gray-300 rounded-lg"
/>
              </div>
              <div className="flex flex-col flex-1 min-w-[200px]">
                <label className="font-medium mb-1">Create Password</label>
                <input
  type="password"
  name="password"
  value={formData.password}
  onChange={handleChange}
  placeholder="Create a password"
  className="p-3 border border-gray-300 rounded-lg"
  onKeyDown={(e) => {
    if (e.key === " ") e.preventDefault(); // block space character
  }}
/>
              </div>
            </div>

            <div className="flex flex-col mb-5">
              <label className="font-medium mb-1">Full Address</label>
              <textarea
                name="address"
                value={formData.address}
                onChange={handleChange}
                placeholder="Enter your full address"
                className="p-3 border border-gray-300 rounded-lg resize-none"
                rows={3}
              ></textarea>
            </div>

            <div className="flex flex-wrap gap-4 mb-5">
              <div className="flex flex-col flex-1 min-w-[100px]">
                <label className="font-medium mb-1">City</label>
                <input
                  type="text"
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                  className="p-3 border border-gray-300 rounded-lg"
                />
              </div>
              <div className="flex flex-col flex-1 min-w-[100px]">
                <label className="font-medium mb-1">State</label>
                <input
                  type="text"
                  name="state"
                  value={formData.state}
                  onChange={handleChange}
                  className="p-3 border border-gray-300 rounded-lg"
                />
              </div>
              <div className="flex flex-col flex-1 min-w-[100px]">
                <label className="font-medium mb-1">Pincode</label>
                <input
                  type="text"
  name="pincode"
  inputMode="numeric"
  value={formData.pincode}
  onChange={handleChange}
  className="p-3 border border-gray-300 rounded-lg"
                />
              </div>
            </div>

            <div className="w-full mb-5">
              <div className="flex items-center gap-2">
                <input
                  type="checkbox"
                  id="agree"
                  name="agree"
                  checked={formData.agree}
                  onChange={handleChange}
                  className="h-4 w-4 accent-indigo-600"
                />
                <label htmlFor="agree" className="text-sm leading-snug">
                  I agree to the{" "}
                  <a href="#" className="text-indigo-600 underline">
                    Terms and Conditions
                  </a>{" "}
                  and{" "}
                  <a href="#" className="text-indigo-600 underline">
                    Privacy Policy
                  </a>
                </label>
              </div>
            </div>

            <button
              type="submit"
              className="bg-indigo-600 text-white font-bold py-3 rounded-lg hover:bg-indigo-700 transition mb-4"
            >
              Register
            </button>

            <p className="text-center text-sm">
              Already have an account?{" "}
              <a href="/login" className="text-indigo-600 hover:underline">
                Login
              </a>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RegistrationBox;
